mapper.select:
select ATTR_CODE as "attrCode", GROUP_CODE as "groupCode", ATTR_NAME as "attrName", ATTR_TYPE as "attrType", OPTIONS as "options", SORT_NO as "sortNo" from c_pawn_attr_conf
GROUP_CODE=#{groupCode}
and ( ATTR_CODE like '%${keyWord}%' or ATTR_NAME like '%${keyWord}%' or ATTR_TYPE like '%${keyWord}%' )
